      `https://gnews.io/api/v4/top-headlines?country=${this.props.country}&lang=en&category=${this.props.category}&token=c59ec6468a508ce0f6e69e1efabcd08e&page=${this.state.page}&pagesize=${this.props.pagesize}`
      this.setState({loading:true});
    let data = await fetch(url);
    console.log(data);
