import React, { Component } from "react";
import PropTypes from "prop-types";
import NewsItem from "./NewsItem";
import Spinner from './Spinner'
// import InfiniteScroll from "react-infinite-scroll-component";

export class News extends Component {
                                         
  static defaultProps={
        country:"in",
        pageSize:8,
        category: 'general',
  }

  static propTypes={
 country: PropTypes.string,
 pageSize:PropTypes.number,
 category: PropTypes.string,
  }
  capitalizeFirstLetter=(string)=>{
    return string.charAt(0).toUpperCase() + string.slice(1);
}                                      

  constructor(props) {
    // Object is passed below, then only constructor will work.
    super(props);
    console.log("This is constructor from NewsItems");
    //"state" Object is created again
    this.state = {
      articles: [],
      loading: false,
      page: 1,
      totalResults:0
    }

    // Title will change according to the category we are reading and a function is used which will make the first letter capital.
      document.title=`${this.capitalizeFirstLetter(this.props.category)} -NewsApp`;
  }
                                                                  
  // Basically we have created an updation function which can be used in previous and forward page 
  async updation() {
    let url =
      `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=e8adac170d874fd296098e16fed2f214&page=${this.state.page}&pageSize=${this.props.pageSize}`;
      this.setState({loading:true});
    let data = await fetch(url);
    console.log(data);
    let parsedData = await data.json();
    console.log(parsedData);
    this.setState({
      articles: parsedData.articles,
      totalResults: parsedData.totalResults,
      loading:false
    });
  };


  // API FETCH has been done below
  // here "componentDidMount()" function runs after the component output had been rendered to the DOM.
  async componentDidMount() {
    // let url =
    //   `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=e8adac170d874fd296098e16fed2f214&page=1&pageSize=${this.props.pageSize}`;
    //   this.setState({loading:true});
    // let data = await fetch(url);
    // console.log(data);
    // let parsedData = await data.json();
    // console.log(parsedData);
    // this.setState({
    //   articles: parsedData.articles,
    //   totalResults: parsedData.totalResults,
    //   loading:false
    // });
    this.updation();
  }
                                                                                                 
  // Move Back
  moveToBack = async () => {
    // console.log("BACK");
    // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=e8adac170d874fd296098e16fed2f214&page=${
    //   this.state.page - 1
    // }&pageSize=${this.props.pageSize}`;
    //  this.setState({loading:true});
    // let data = await fetch(url);
    // let parsedData = await data.json();
    // // console.log(parsedData);
    // this.setState({
    //   page: this.state.page - 1,
    //   articles: parsedData.articles,
    //   loading:false
    // });
  this.setState({page: this.state.page-1});
  this.updation();
  };
                                                                 
  // Move Next
  moveToNext = async () => {
    console.log("NEXT");
    // Math ceil will give the round off or integer value of the next bigger number
    if (this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)) {
    } 
    else {
      // let url = `https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=e8adac170d874fd296098e16fed2f214&page=${
      //   this.state.page + 1
      // }&pageSize=${this.props.pageSize}`;
      // this.setState({loading:true});
      // let data = await fetch(url);
      // let parsedData = await data.json();
      // console.log(parsedData.articles);
      // this.setState({
      //   page: this.state.page + 1,
      //   articles: parsedData.articles,
      //   loading:false
      // });
      this.setState({page: this.state.page+1});
      this.updation();
    }
  };
                                                                    
  render() {
    // useEffect(() => {
    //   moveToBack();
    // },[])
    return (
      <div className="container my-3">
        <h1 className="text-center my-4">
          {" "}
          <strong>Our NewsApp-Top {this.capitalizeFirstLetter(this.props.category)} headlines</strong>{" "}
         {/* {this.state.loading &&<Spinner/>} */}
        </h1>
        
        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length!==this.state.totalResults}
          loader={<Spinner/>}
        >
              
        <div className="row">
          {/* When we use higher order arrays maping, we generally use key to differentiate all the elements AND 
    The slice() method returns selected elements in an array, as a new array.*/}
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4 my-2" key={element.url}>
                <NewsItem 
                  title={element.title ? element.title.slice(0, 50) : ""}
                  description={ element.description ? element.description.slice(0, 190) : ""}
                  imageUrl={element.urlToImage}
                  newsURL={element.url}
                  author={element.author}
                  date = {element.publishedAt}
                  name = {element.source.name}
                />
              </div>
            );
          })}
        </div>
        </InfiniteScroll>
    
        <div className="container d-flex justify-content-between">
          <button
            disabled={this.state.page <= 1}
            type="button"
            className="btn btn-dark"
            onClick={this.moveToBack}
          >
            &larr; Back
          </button>
          <button
            disabled={
              this.state.page + 1 > Math.ceil(this.state.totalResults / this.props.pageSize)
            }
            type="button"
            className="btn btn-dark"
            onClick={this.moveToNext}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
