// import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react'
import Navbar from './Components/Navbar';
import News from './Components/News';
import LoadingBar from 'react-top-loading-bar'
// import NewsItem from './Components/NewsItem';
import {
  BrowserRouter as Router,
  Switch,
  Route,
} from "react-router-dom";
// import { BrowserRouter as Router, Routes, Route } from "react-router-dom"
// import { Switch } from 'react-router-dom';
export default class App extends Component {
  pageSize=6;   

  State={
    progress:0
  }
  setProgress(progress){
    this.setState({progress:progress})
  }

  render() {
    return (
      <Router>
      <div>
      <Navbar />


{/* Loading bar  */}
      <LoadingBar
        color='#f11946'
        progress={this.state.progress}
      />
      {/* <News pageSize = {this.pageSize}  country= "in" category= "science" /> */}
      {/* <NewsItem/> */}

{/* Here below an individual key is used so that every link can be differentiated with each other. */}
      <Switch>

          <Route exact path="/"><News setProgress={setProgress} key="general" pageSize = {this.pageSize} country= "in" category= "general"/></Route>
          <Route exact path="/business"><News setProgress={setProgress} key="business" pageSize = {this.pageSize} country="in" category="business"/></Route>
          <Route exact path="/entertainment"><News setProgress={setProgress} key="entertainment" pageSize = {this.pageSize} country= "in" category="entertainment"/></Route>
          <Route exact path="/health"><News setProgress={setProgress} key="health" pageSize = {this.pageSize} country= "in" category="health"/></Route>
          <Route exact path="/science"><News setProgress={setProgress} key="science" pageSize = {this.pageSize} country= "in" category="science"/></Route>
          <Route exact path="/sports"><News setProgress={setProgress} key="sports" pageSize = {this.pageSize} country= "in" category="sports"/></Route>
          <Route exact path="/technology"><News setProgress={setProgress} key="technology"  pageSize = {this.pageSize} country= "in" category="technology"/></Route>

        </Switch>     
        </div>
        </Router>
       )
  }
}
