import React, { Component } from 'react'
import PropTypes from 'prop-types'
import NewsItem from './NewsItem'

export class News extends Component {
        
          constructor(){
        
            // Object is passed below, then only constructor will work.
            super();
            console.log("This is constructor from NewsItems")
            //"state" Object is created again 
            this.state = {
                 articles: [],
                 loading:false
            }
          }
          
          // API FETCH 
          async componentDidMount(){
            let url  = "https://newsapi.org/v2/top-headlines?country=in&apiKey=e8adac170d874fd296098e16fed2f214"
            let data =  await fetch(url)
            let parsedData =await data.json()
            console.log(parsedData)
            this.setState({articles: parsedData.articles})
          }

  moveToBack=()=>{

  }
  
  moveTo

  render() {
    return (
           
<div className="container my-3">
<strong><h1>Top headlines</h1></strong>
<div className="row">
    {/* When we use higher order arrays maping, we generally use key to differentiate all the elements AND 
    The slice() method returns selected elements in an array, as a new array.*/}
{this.state.articles.map((element)=>{ 
    return <div className="col-md-4" key={element.url}>
        <NewsItem  title= {element.title?element.title:""} description={element.description?element.description:""} imageUrl={element.urlToImage} newsURL={element.url} />
    </div>
})}
</div>
     <div className="container d-flex justify-content-between">
     <button disabled={this.state.page<=1} type="button" class="btn btn-dark" onClick={this.moveToBack}>&larr; Back</button>
     <button type="button" class="btn btn-dark" onClick={this.moveToNext}>Next &rarr;</button>

     </div>
        </div>
    )
  }
}

export default News
